'                -- get values for APEX items',
'                dbms_sql.bind_variable(l_curs, l_binds, v(l_binds));',
'        end case;',
'    end loop;',
'',
'    l_exec := dbms_sql.execute(l_curs);',
'    dbms_sql.variable_value(l_curs, ''P_OUT_PK'', p_out_pk);',
'exception',
'    when others then',
'        if dbms_sql.is_open(l_curs) then',
